targets.expr:
max by (short_uri) (
  label_replace(
    http_server_requests_seconds{
      container="ais-custom-crm-api",
      quantile="0.9",
      uri!~"/|/metrics|.*health.*|.*swag.*|.*docs.*",
      uri=~"$uri",
      outcome="SUCCESS"
    } > 0.999,
    "short_uri", "$1$2", "uri", "^(?:/api/ais-crm/v1\\.0/([^/?]+)|(.*))"
  )
)
